import React from "react";
import { StyleSheet, Text, View, FlatList, Alert, TouchableOpacity } from "react-native";

const FlatListScreen = () => {

    const student2 = [
        { name: "Урангоо", color: "#21618C",id: 1 },
        { name: "Содхүү", color: "#2874A6",id: 2 },
        { name: "Гантулга", color: "#2E86C1",id: 3 },
        { name: "Нацагдорж", color: "#3498DB",id: 4 },
        { name: "Баяндалай", color: "#5DADE2",id: 5 },
        { name: "Мөнхжин", color: "#85C1E9",id: 6 },
        { name: "Соёлзул", color: "#AED6F1",id: 7 },
        { name: "Балжинням", color: "#D6EAF8",id: 8 },
        { name: "Эрхэмбаяр", color: "#EBF5FB",id: 9 }
    ];

    const student3 = [
        { name: "Урангоо", color: "#21618C",id: 1 },
        { name: "Содхүү", color: "#2874A6",id: 2 },
        { name: "Гантулга", color: "#2E86C1",id: 3 },
        { name: "Нацагдорж", color: "#3498DB",id: 4 },
        { name: "Баяндалай", color: "#5DADE2",id: 5 },
        { name: "Мөнхжин", color: "#85C1E9",id: 6 },
        { name: "Соёлзул", color: "#AED6F1",id: 7 },
        { name: "Балжинням", color: "#D6EAF8",id: 8 },
        { name: "Эрхэмбаяр", color: "#EBF5FB",id: 9 }
    ];
    const student4 = [
        { name: "Урангоо", color: "#21618C",id: 1 },
        { name: "Содхүү", color: "#2874A6",id: 2 },
        { name: "Гантулга", color: "#2E86C1",id: 3 },
        { name: "Нацагдорж", color: "#3498DB",id: 4 },
        { name: "Баяндалай", color: "#5DADE2",id: 5 },
        { name: "Мөнхжин", color: "#85C1E9",id: 6 },
        { name: "Соёлзул", color: "#AED6F1",id: 7 },
        { name: "Балжинням", color: "#D6EAF8",id: 8 },
        { name: "Эрхэмбаяр", color: "#EBF5FB",id: 9 }
    ];
    const handClick = name => {
        console.log(`Hi:${name}`);
    }
    return (
        <view>
            <Text>it</Text>
        <View>
        <FlatList
        horizontal={true}
        showsHorizontalScrollIndicator={false}
            keyExtractor={student2 => student2.id}
            data={student2}
            renderItem={({ item, index }) => (
                <TouchableOpacity
                    onPress={() => handClick(item.name)}
                >
                    <View style={{
                        backgroundColor: item.color,
                        padding: 10,
                        marginHorizontal: 20
                    }}
                    >
                        <Text>
                            {index + 1} {item.name}
                        </Text>
                    </View>
                </TouchableOpacity>
            )}
        /></View><Text>it</Text><View>
        <FlatList
        horizontal={true}
        showsHorizontalScrollIndicator={false}
            keyExtractor={student3 => student3.id}
            data={student3}
            renderItem={({ item, index }) => (
                <TouchableOpacity
                    onPress={() => handClick(item.name)}
                >
                    <View style={{
                        backgroundColor: item.color,
                        padding: 10,
                        marginHorizontal: 20
                    }}
                    >
                        <Text>
                            {index + 1} {item.name}
                        </Text>
                    </View>
                </TouchableOpacity>
            )}
        /></View><Text>it</Text><View>
        <FlatList
        horizontal={true}
        showsHorizontalScrollIndicator={false}
            keyExtractor={student4 => student4.id}
            data={student4}
            renderItem={({ item, index }) => (
                <TouchableOpacity
                    onPress={() => handClick(item.name)}
                >
                    <View style={{
                        backgroundColor: item.color,
                        padding: 10,
                        marginHorizontal: 20
                    }}
                    >
                        <Text>
                            {index + 1} {item.name}
                        </Text>
                    </View>
                </TouchableOpacity>
            )}
        /></View></view>
    );
};
export default FlatListScreen
const style = StyleSheet.create({});
